import React from 'react';

function Text() {
    const words = ['Grapic', 'web'];

    const p = document.querySelector('p');
    var newHTML = p.innerHTML
    words.forEach(word =>
        newHTML = newHTML.replace(word, `<span class="color">${word}</span>`)
    )
    p.innerHTML = newHTML;




    return (


        <p className='a'>
            {document.querySelector("p").indexOf("Graphic").style.color = "red"}
            Graphic and web design are far more than a job for me.Thanks to my extensive
            technical knowledge , I can implement my creative ideas very well,so that I can
            offer tailor-made solutions for customers.
        </p>
    );
}

export default Text;
