import React from 'react';



function Pictures(props) {
    const element = document.querySelector(".pic");
    return (

        <div className='pic'>
            {props.p.website === false ? element.style.backgroundColor = `${props.p.c}` : element.style.backgroundColor = `${props.p.c}`}

            <img src={props.p.img} alt={`Icon of ${props.p.media}`} />
            <p>{props.p.name}</p>
            <p>{props.p.work}</p>
            <p>{props.p.phone}</p>
            <p>{props.p.email}</p>
            <p>
                {props.p.website}
            </p>

        </div>
    );
}

export default Pictures;
