import React from 'react';

function Text() {
    const cliento = "Designer";
    // const title_designer = "Design ";
    // const title_programmer = "Programming ";

    if (cliento === "Designer") {
        document.querySelector("p").indexOf("Garphic").style.color = "blue";
    }

    return (

        <p>
            Graphic and web design are far more than a job for me.Thanks to my extensive
            technical knowledge , I can implement my creative ideas very well,so that I can
            offer tailor-made solutions for customers.
        </p>
    );
}

export default Text;
