//class component with state


import React, { Component } from 'react';



class Pictures extends Component {
    render() {
        return (
            <div className='col-md-4 col-sm-6'>
                <div className="card">
                    <div className="card-header">
                        <img style={{ maxWidth: '100%' }} src={this.state.img} alt={`Icon of `} />
                    </div>
                    <div className='card-body'
                        style={{ backgroundColor: this.state.website ? 'white' : 'red' }}>
                        <p>{this.state.name}</p>
                        <p>{this.state.work}</p>
                        <p>{this.state.phone}</p>
                        <p> {this.state.email ? "" : 'there is no email'}
                            {this.state.email}</p>
                        <p>{this.state.website}</p>
                    </div>
                </div>

            </div>


        );
    }
}

export default Pictures;












