import React, { Component } from 'react';




class Statos extends Component {

    constructor() {
        super()
        this.state = {
            id: 1,
            img: 'https://picsum.photos/60',
            name: 'omarz',
            work: 'web designer',
            phone: '+49 123456',
            email: 'omarz@gmail.com',
        }
    }



    render() {
        return (

            <div>
                <div className='col-md-4 col-sm-6'>
                    <div className="card">
                        <div className="card-header">
                            <img style={{ maxWidth: '100%' }} src={this.state.img} alt={`Icon of `} />
                        </div>
                        <div className='card-body'
                            style={{ backgroundColor: this.state.website ? 'white' : 'red' }}>
                            <p>{this.state.name}</p>
                            <p>{this.state.work}</p>
                            <p>{this.state.phone}</p>
                            <p> {this.state.email ? "" : 'there is no email'}
                                {this.state.email}</p>
                            <p>{this.state.website}</p>
                        </div>
                    </div>

                </div>
            </div>

        )
    }





}


export default Statos
