import React from 'react';



function Pictures(props) {
    return (
        <div className='col-md-4 col-sm-6'>
            <div className="card">
                <div className="card-header">
                    <img style={{ maxWidth: '100%' }} src={props.info.img} alt={`Icon of `} />
                </div>
                <div className='card-body'
                    style={{ backgroundColor: props.info.website ? 'white' : 'red' }}>
                    <p>{props.info.name}</p>
                    <p>{props.info.work}</p>
                    <p>{props.info.phone}</p>
                    <p> {props.info.email ? "" : 'there is no email'}
                        {props.info.email}</p>
                    <p>{props.info.website}</p>
                </div>
            </div>

        </div>
    );
}

export default Pictures;
