import React from 'react';


function Text() {
    function changeSubStringColor() {
        var str = document.querySelector("p").innerHTML;
        var res = str.replace("and", "<span style='color:red'>CodeProject</span>");
        document.querySelector("p").innerHTML = res;
    }
    return (
        <p>
            {changeSubStringColor()}
            Graphic and web design
        </p>
    );
}

export default Text;
